        // This handles the case where the object is inside a scrolled div.
        while (obj && obj != document.body) {
            curleft -= obj.scrollLeft;
            curtop -= obj.scrollTop;
            obj = obj.parentNode;
        }
        return {x: curleft, y: curtop};
